import React , { useState,useEffect  } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Paper from '@material-ui/core/Paper';
import Fab from '@material-ui/core/Fab';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import ListSubheader from '@material-ui/core/ListSubheader';
import Avatar from '@material-ui/core/Avatar';
import MenuIcon from '@material-ui/icons/Menu';
import AddIcon from '@material-ui/icons/Add';
import SearchIcon from '@material-ui/icons/Search';
import MoreIcon from '@material-ui/icons/MoreVert';
import axios from 'axios';
import { StyleSheet, Text, View, Image, TextInput, ActivityIndicator, TouchableOpacity} from 'react-native';
import Modal from 'modal-enhanced-react-native-web';
import { Card, Button } from 'react-native-elements';
import { FAB } from 'react-native-paper';
import Add from '../comp/Add';
const width_proportion = '15%';
const height_proportion = '25%';
const useStyles = makeStyles((theme) => ({
  text: {
    padding: theme.spacing(2, 2, 0),
  },
  paper: {
    paddingBottom: 50,
  },
  list: {
    marginBottom: theme.spacing(2),
  },
  subheader: {
    backgroundColor: theme.palette.background.paper,
  },
  appBar: {
    top: 'auto',
    bottom: 0,
  },
  grow: {
    flexGrow: 1,
  },
  fabButton: {
    position: 'absolute',
    zIndex: 1,
    top: -30,
    left: 0,
    right: 0,
    margin: '0 auto',
  },
}));

export default function AppbarA() {
  const classes = useStyles();


 
 


  return (
    <React.Fragment>
     <View style={styles.b}><Text  style={styles.b} >Services </Text>
        
   
    


      </View>
      <CssBaseline />
      <Paper square className={classes.paper}>
     
      <TouchableOpacity
         style={styles.btn}
        
       >
         <Text> Missions </Text>
 </TouchableOpacity>
        
      </Paper>
      <AppBar position="sticky" color="primary" className={classes.appBar}>
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="open drawer">
            <MenuIcon />
          </IconButton>
       
          
          <div className={classes.grow} />
          <IconButton color="inherit">
            <SearchIcon />
          </IconButton>
          <IconButton edge="end" color="inherit">
            <MoreIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
    </React.Fragment>
    
  );
}

const styles = StyleSheet.create({
  b: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
   
    fontWeight: "bold" ,
    fontSize:30
   
  },
  c :{
    backgroundColor: '#fff',
  },

 fab:{
 
  position: 'absolute',
  margin: 20,
  right: 140,
  bottom: 0,
  zIndex: 1,
  
  
 
 },
input:{
borderWidth:1,
borderColor: '#777',
padding:8,
margin:10,
width:200,
borderRadius:10,

},
btn: {

  marginLeft: width_proportion,
  marginRight: width_proportion,
  marginTop:15,
  borderRadius: 10,
  backgroundColor: '#ff291e',
  padding: 11
  
  
   
}



});
