import { StyleSheet, Text, View, Image, TextInput} from 'react-native';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/core/styles';
import React, { useState, useEffect } from 'react';
import axios from 'axios';


const useStyles = makeStyles((theme) => ({
    button: {
      margin: theme.spacing(1),
      marginLeft: 40,
      marginRight: 40,
      marginBottom: 15,
      height:40,

      alignItems : theme.center,
    },
    butto: {
        margin: theme.spacing(1),
        
        marginLeft: 40,
        marginRight: 40,
        marginBottom: 15,
        marginTop:70,
        alignItems : theme.center,
      },
      appBar: {
        top: 'auto',
        bottom: 0,
      },
  }));
  


export default function Profile({navigation}) {

    const [data, setData] = useState({ hits: [] });
    const hid = navigation.getParam('id');

    useEffect(() => {
        const result = axios.post('http://localhost:3000/api/User/data',{
            mail: hid,
         
            })
       
          setData(result.data);
      },)




    const pressHandler = () =>{

        navigation.navigate('Dash') 

    }
   


    const classes = useStyles();
    return (
   
        <View  >
            <ul>
      {data.hits.map(item => (
        <li key={item._id}>
         <Text>{item.name}</Text>
        </li>
      ))}
    </ul>
          <Text  style={styles.b} >{navigation.getParam('id')} </Text> 
       
       
      <View  position= "fixed" style={styles.headerFooterStyle}>
     <Text style={styles.textStyle}>    CopyrightÂ© NGI 2021</Text>
      </View>
     
     </View>
 
  );
}

const styles = StyleSheet.create({
  b: {
   
    
    backgroundColor: '#fff',
   
    fontWeight: "bold" ,
    fontSize:30
   
  },
  bt: {
   marginTop:"50"
  },
  headerFooterStyle: {
    width: '100%',
  
    bottom:0,
   
  },
  textStyle: {
    textAlign: 'center',
    color: '#0f0f0f',
    fontSize: 12,
    marginTop:15,
    padding: 115,
  },
  b: {
   
    justifyContent: 'center',
