   if(vall){

      axios.post('http://localhost:3000/api/user/register',{
        name: name,
          password: password,
          mail: mail,
        }).then(res => {
         
          setD(res.data)
           
    
        })
        setvall(false);
} 

    
   
  if (dataS) {
  setval(true)
  }
  
  if(val){
    alert(dataS)
    setval(false)
    return(null)
  }
   
       return(null)
    
  
    };



    
   
    


